    processing_pieces--;
}

template.declaration:
clock x;
int processing_time;
belt_t input, output;

void build() {
    processing_time = station_conf[id].processing_time;
    input = station_conf[id].input;
    output = station_conf[id].output;
}

belt_t belt;
pos_t pos;
station_t station;

void build() {
    belt = tail_sensor_conf[id].belt;
    pos = tail_sensor_conf[id].pos;
    station = tail_sensor_conf[id].station;
}

void update() {
    wait[belt] = belts[belt][pos];
}

belt_t belt;
pos_t pos;
station_t station;

void build() {
    belt = head_sensor_conf[id].belt;
    pos = head_sensor_conf[id].pos;
    station = head_sensor_conf[id].station;
}

pos_t head;
pos_t tail;
pos_t length;
station_t station;

void build() {
    head = belt_conf[id].head;
    tail = belt_conf[id].tail;
    length = belt_conf[id].length;
}

// I need two methods, one that updates from the first segment to the head sensor and one that updates everything else
// this second method if the head is -1 needs to do nothing since the first method has already taken care of everything
void move_head() {
    int i;
    int limit = (head == -1)?length:head;

    for (i = 1; i < limit; i++) {
        if (belts[id][i-1] == 0) {
            belts[id][i-1] = belts[id][i];
            belts[id][i] = 0;
        }
    }
}

void move_tail() {
    int i;

    if (head != -1) {
        for (i = head+1; i < length; i++) {
            if (belts[id][i-1] == 0) {
                belts[id][i-1] = belts[id][i];
                belts[id][i] = 0;
            }
        }
    }
}


void move_belt() {
    move_head();

    if (gate[id]) {
        if (head > 0 && belts[id][head-1] == 0) {
            belts[id][head-1] = belts[id][head];
            belts[id][head] = 0;
        }
    }

    move_tail();
}

void pop() {
    belts[id][0] -= 1;
}

bool head_occupied() {
    return belts[id][0];
}

void add_last() {
    belts[id][length-1] += 1;
}

clock x;

void init_all() {
    int i, j;

    for (i = 0; i < BELTS; i++) {
        if (belt_conf[i].head == -1) {
            for (j = 0; j < belt_conf[i].pieces; j++) {
                belts[i][i] = 1;
            }
        }
        else {
            for (j = belt_conf[i].head; j < belt_conf[i].pieces + belt_conf[i].head; j++) {
                belts[i][j] = 1;
            }
        }

        pieces_on_belt += belt_conf[i].pieces;
    }
    total_pieces = pieces_on_belt;
}

void update_count() {
    int i, j;
    int app = 0;

    for (i = 0; i < BELTS; i++) {
        for(j = 0; j < belt_conf[i].length;j++) {
            app += belts[i][j];
        }
    }

    pieces_on_belt = app;
}

