typedef int[-1, MAX_SLOTS] pos_t;
typedef int[1, 100] error_t;

typedef struct { 
    pos_t head, tail, length;
    int pieces;
} belt_config_t;

typedef struct {
    belt_t input, output;
    int mean, variance;
} station_config_t;

