void init_all() {
    int i, j;

    for (i = 0; i < BELTS; i++) {
        if (belt_conf[i].head == -1) {
            for (j = 0; j < belt_conf[i].pieces; j++) {
                belts[i][i] = 1;
            }
        }
        else {
            for (j = belt_conf[i].head; j < belt_conf[i].pieces + belt_conf[i].head; j++) {
                belts[i][j] = 1;
            }
        }

        //pieces_on_belt += belt_conf[i].pieces;
    }
    //total_pieces = pieces_on_belt;
}

